"use strict";
let array = require('./index.js');

let test = [];

/**
 * Fill array
 */
while (true) {
    test.push(0);

    if (test.length > 1024) {
        break;
    }
}

/**
 * Walk array
 */
array.nsplay(test, function (item) {
    process.stdout.write(`${item}|`);
    return item;
}, function (arr) {
    console.log(arr);
});
